      slug: "que-es-la-tecnologia-rfid",
      titulo: "¿Qué es la tecnología RFID?",
      etiquetas: [
        "Barazaletes",
        "Brazaletes Mexico",
        "Brazaletes para Hoteles",
        "Brazaletes RFID",
